public class Member extends Customer {

    private String name;
    private String id;
    private int loyaltyPoints = 0;
    private boolean redeemable = false;

    public Member() {
    }

    public Member(String name, String id) {
    	this.name = name;
    	this.id = id;
    }

    public String getId() {
    	return id;
    }


    public String getName() {
    	return name;
    }

    public void setName(String name) {
    	this.name = name;
    }

    public void setId(String id) {
    	this.id = id;
    }

    public int getLoyaltyPoints() {
    	return loyaltyPoints;
    }

    public boolean isRedeemable() {
		return redeemable;
	}

	public void setRedeemable(boolean redeemable) {
		this.redeemable = redeemable;
	}

	public void addLoyaltyPoints(int points) {
    	this.loyaltyPoints += points;
    }

    // Deduct LoyaltyPoints only when the member has enough points.
    public void deductLoyaltyPoints(int points) {
    	int tempPts = this.loyaltyPoints - points;
    	if(tempPts >= 0) this.loyaltyPoints = tempPts;
    }

    public String toString(){
    	return this.name + "," + this.id + "," + this.loyaltyPoints;
    }
}
